import React, { useState } from "react";
import { Link, useN } from "react-router-dom";
import { Container, Typography, TextField, Button, Grid } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(8),
    marginBottom: theme.spacing(8),
  },
  form: {
    marginTop: theme.spacing(2),
  },
  submitButton: {
    marginTop: theme.spacing(2),
  },
  loginLink: {
    marginTop: theme.spacing(2),
  },
}));

const StudentRegistrationForm = () => {
  const classes = useStyles();
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
  });
  const history = useHistory();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Implement logic to submit form data to server
    // Redirect to student dashboard after successful registration
    history.push("/student/dashboard");
  };

  return (
    <Container maxWidth="xs" className={classes.container}>
      <Typography variant="h4" align="center" gutterBottom>
        Student Registration
      </Typography>
      <form onSubmit={handleSubmit} className={classes.form}>
        <TextField
          label="First Name"
          fullWidth
          name="firstName"
          value={formData.firstName}
          onChange={handleChange}
          margin="normal"
        />
        <TextField
          label="Last Name"
          fullWidth
          name="lastName"
          value={formData.lastName}
          onChange={handleChange}
          margin="normal"
        />
        <TextField
          label="Email"
          fullWidth
          name="email"
          type="email"
          value={formData.email}
          onChange={handleChange}
          margin="normal"
        />
        <TextField
          label="Password"
          fullWidth
          name="password"
          type="password"
          value={formData.password}
          onChange={handleChange}
          margin="normal"
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          fullWidth
          className={classes.submitButton}
        >
          Register
        </Button>
      </form>
      <Typography variant="body1" align="center" className={classes.loginLink}>
        Already have an account?{" "}
        <Link to="/login">
          Login
        </Link>
      </Typography>
    </Container>
  );
};

export default StudentRegistrationForm;
