import React from "react";
import { useHistory } from "react-router-dom";
import { Container, Typography, Button, Grid } from "@material-ui/core";

const Home = () => {
  const history = useHistory();

  const handleLoginClick = () => {
    // Redirect to login page
    history.push("/login");
  };

  const handleRegisterClick = () => {
    // Redirect to register page
    history.push("/register");
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h4" align="center" gutterBottom>
        Welcome to the Exam Portal
      </Typography>
      <Typography variant="body1" align="center" gutterBottom>
        About Exam Portal: [Simply Powerful ONLINE EXAMS Exam.net equips you
        with all the power and function you need to securely create, conduct and
        mark exams for your students. Create and conduct your first exam in less
        than an hour.]
      </Typography>
      <Typography variant="h6" align="center" gutterBottom>
        Login
      </Typography>
      <Grid container spacing={2} justifyContent="center">
        <Grid item xs={12} sm={6}>
          <Button
            variant="contained"
            color="primary"
            fullWidth
            onClick={handleLoginClick}
          >
            Student Login
          </Button>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Button
            variant="contained"
            color="secondary"
            fullWidth
            onClick={handleLoginClick}
          >
            Admin Login
          </Button>
        </Grid>
      </Grid>
      <Typography variant="h6" align="center" gutterBottom>
        Register
      </Typography>
      <Typography variant="body1" align="center" gutterBottom>
        If you're a new student, please register here:
      </Typography>
      <Grid container spacing={2} justifyContent="center">
        <Grid item xs={12} sm={6}>
          <Button
            variant="contained"
            color="primary"
            fullWidth
            onClick={handleRegisterClick}
          >
            Register as Student
          </Button>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Home;
